declare(strict_types=1);

namespace Zikula\RoutesModule\Menu\Base;

use Knp\Menu\FactoryInterface;
use Knp\Menu\ItemInterface;
use Symfony\Contracts\EventDispatcher\EventDispatcherInterface;
use Symfony\Component\HttpFoundation\RequestStack;
use Symfony\Contracts\Translation\TranslatorInterface;
use Zikula\Common\Translator\TranslatorTrait;
use Zikula\ExtensionsModule\Api\ApiInterface\VariableApiInterface;
use Zikula\UsersModule\Api\ApiInterface\CurrentUserApiInterface;
use Zikula\UsersModule\Constant as UsersConstant;
use Zikula\RoutesModule\Entity\RouteEntity;
use Zikula\RoutesModule\RoutesEvents;
use Zikula\RoutesModule\Event\ConfigureItemActionsMenuEvent;
use Zikula\RoutesModule\Event\ConfigureViewActionsMenuEvent;
use Zikula\RoutesModule\Helper\EntityDisplayHelper;
use Zikula\RoutesModule\Helper\ModelHelper;
use Zikula\RoutesModule\Helper\PermissionHelper;

/**
 * Menu builder base class.
 */
class AbstractMenuBuilder
{
    use TranslatorTrait;
    
    /**
     * @var FactoryInterface
     */
    protected $factory;
    
    /**
     * @var EventDispatcherInterface
     */
    protected $eventDispatcher;
    
    /**
     * @var RequestStack
     */
    protected $requestStack;
    
    /**
     * @var PermissionHelper
     */
    protected $permissionHelper;
    
    /**
     * @var EntityDisplayHelper
     */
    protected $entityDisplayHelper;
    
    /**
     * @var CurrentUserApiInterface
     */
    protected $currentUserApi;
    
    /**
     * @var VariableApiInterface
     */
    protected $variableApi;
    
    /**
     * @var ModelHelper
     */
    protected $modelHelper;
    
    public function __construct(
        TranslatorInterface $translator,
        FactoryInterface $factory,
        EventDispatcherInterface $eventDispatcher,
        RequestStack $requestStack,
        PermissionHelper $permissionHelper,
        EntityDisplayHelper $entityDisplayHelper,
        CurrentUserApiInterface $currentUserApi,
        VariableApiInterface $variableApi,
        ModelHelper $modelHelper
    ) {
        $this->setTranslator($translator);
        $this->factory = $factory;
        $this->eventDispatcher = $eventDispatcher;
        $this->requestStack = $requestStack;
        $this->permissionHelper = $permissionHelper;
        $this->entityDisplayHelper = $entityDisplayHelper;
        $this->currentUserApi = $currentUserApi;
        $this->variableApi = $variableApi;
        $this->modelHelper = $modelHelper;
    }
    
    /**
     * Builds the item actions menu.
     */
    public function createItemActionsMenu(array $options = []): ItemInterface
    {
        $menu = $this->factory->createItem('itemActions');
        if (!isset($options['entity'], $options['area'], $options['context'])) {
            return $menu;
        }
    
        $entity = $options['entity'];
        $routeArea = $options['area'];
        $context = $options['context'];
        $menu->setChildrenAttribute('class', 'nav item-actions');
    
        $this->eventDispatcher->dispatch(
            new ConfigureItemActionsMenuEvent($this->factory, $menu, $options),
            RoutesEvents::MENU_ITEMACTIONS_PRE_CONFIGURE
        );
    
        if ($entity instanceof RouteEntity) {
            $routePrefix = 'zikularoutesmodule_route_';
            
            if ('admin' === $routeArea) {
                $title = $this->trans('Preview');
                $previewRouteParameters = $entity->createUrlArgs();
                $previewRouteParameters['preview'] = 1;
                $menu->addChild($title, [
                    'route' => $routePrefix . 'display',
                    'routeParameters' => $previewRouteParameters
                ]);
                $menu[$title]->setLinkAttribute('target', '_blank');
                $menu[$title]->setLinkAttribute(
                    'title',
                    $this->trans('Open preview page')
                );
                $menu[$title]->setAttribute('icon', 'fas fa-search-plus');
            }
            if ('display' !== $context) {
                $title = $this->trans('Details');
                $menu->addChild($title, [
                    'route' => $routePrefix . $routeArea . 'display',
                    'routeParameters' => $entity->createUrlArgs()
                ]);
                $entityTitle = $this->entityDisplayHelper->getFormattedTitle($entity);
                $menu[$title]->setLinkAttribute(
                    'title',
                    str_replace('"', '', $entityTitle)
                );
                $menu[$title]->setAttribute('icon', 'fas fa-eye');
            }
            if ($this->permissionHelper->mayEdit($entity)) {
                $title = $this->trans('Edit');
                $menu->addChild($title, [
                    'route' => $routePrefix . $routeArea . 'edit',
                    'routeParameters' => $entity->createUrlArgs()
                ]);
                $menu[$title]->setLinkAttribute(
                    'title',
                    $this->trans('Edit this route')
                );
                $menu[$title]->setAttribute('icon', 'fas fa-edit');
                $title = $this->trans('Reuse');
                $menu->addChild($title, [
                    'route' => $routePrefix . $routeArea . 'edit',
                    'routeParameters' => ['astemplate' => $entity->getKey()]
                ]);
                $menu[$title]->setLinkAttribute(
                    'title',
                    $this->trans('Reuse for new route')
                );
                $menu[$title]->setAttribute('icon', 'fas fa-files-o');
            }
            if ('display' === $context) {
                $title = $this->trans('Routes list');
                $menu->addChild($title, [
                    'route' => $routePrefix . $routeArea . 'view'
                ]);
                $menu[$title]->setLinkAttribute('title', $title);
                $menu[$title]->setAttribute('icon', 'fas fa-reply');
            }
        }
    
        $this->eventDispatcher->dispatch(
            new ConfigureItemActionsMenuEvent($this->factory, $menu, $options),
            RoutesEvents::MENU_ITEMACTIONS_POST_CONFIGURE
        );
    
        return $menu;
    }
    
    /**
     * Builds the view actions menu.
     */
    public function createViewActionsMenu(array $options = []): ItemInterface
    {
        $menu = $this->factory->createItem('viewActions');
        if (!isset($options['objectType'], $options['area'])) {
            return $menu;
        }
    
        $objectType = $options['objectType'];
        $routeArea = $options['area'];
        $menu->setChildrenAttribute('class', 'nav view-actions');
    
        $this->eventDispatcher->dispatch(
            new ConfigureViewActionsMenuEvent($this->factory, $menu, $options),
            RoutesEvents::MENU_VIEWACTIONS_PRE_CONFIGURE
        );
    
        $query = $this->requestStack->getMasterRequest()->query;
        $currentTemplate = $query->getAlnum('tpl', '');
        if ('route' === $objectType) {
            $routePrefix = 'zikularoutesmodule_route_';
            if (!in_array($currentTemplate, [])) {
                $canBeCreated = $this->modelHelper->canBeCreated($objectType);
                if ($canBeCreated) {
                    if ($this->permissionHelper->hasComponentPermission($objectType, ACCESS_EDIT)) {
                        $title = $this->trans('Create route');
                        $menu->addChild($title, [
                            'route' => $routePrefix . $routeArea . 'edit'
                        ]);
                        $menu[$title]->setLinkAttribute('title', $title);
                        $menu[$title]->setAttribute('icon', 'fas fa-plus');
                    }
                }
                $routeParameters = $query->all();
                if (1 === $query->getInt('own')) {
                    $routeParameters['own'] = 1;
                } else {
                    unset($routeParameters['own']);
                }
                if (1 === $query->getInt('all')) {
                    unset($routeParameters['all']);
                    $title = $this->trans('Back to paginated view');
                } else {
                    $routeParameters['all'] = 1;
                    $title = $this->trans('Show all entries');
                }
                $menu->addChild($title, [
                    'route' => $routePrefix . $routeArea . 'view',
                    'routeParameters' => $routeParameters
                ]);
                $menu[$title]->setLinkAttribute('title', $title);
                $menu[$title]->setAttribute('icon', 'fas fa-table');
                if ($this->permissionHelper->hasComponentPermission($objectType, ACCESS_EDIT)) {
                    $routeParameters = $query->all();
                    if (1 === $query->getInt('own')) {
                        unset($routeParameters['own']);
                        $title = $this->trans('Show also entries from other users');
                        $icon = 'users';
                    } else {
                        $routeParameters['own'] = 1;
                        $title = $this->trans('Show only own entries');
                        $icon = 'user';
                    }
                    $menu->addChild($title, [
                        'route' => $routePrefix . $routeArea . 'view',
                        'routeParameters' => $routeParameters
                    ]);
                    $menu[$title]->setLinkAttribute('title', $title);
                    $menu[$title]->setAttribute('icon', 'fas fa-' . $icon);
                }
            }
        }
    
        $this->eventDispatcher->dispatch(
            new ConfigureViewActionsMenuEvent($this->factory, $menu, $options),
            RoutesEvents::MENU_VIEWACTIONS_POST_CONFIGURE
        );
    
        return $menu;
    }
}
